  render() {
    return (
      <div className="App">
        <Table items={ this.state.items } delete={this.deleteRow}/>
        <Form handleFormSubmit={ this.handleFormSubmit } 
          handleInputChange={ this.handleInputChange }
          name={ this.state.name }
          age={ this.state.age }
          message={ this.state.message } />

      </div>
    );
  }
}

export default App;
